package toy.cookingstar.web.filter;import org.springframework.boot.web.servlet.FilterRegistrationBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.filter.ShallowEtagHeaderFilter;@Configurationpublic class FilterConfig {    @Bean    public FilterRegistrationBean filterRegistrationBean() {        FilterRegistrationBean<ShallowEtagHeaderFilter> filterRegistrationBean = new FilterRegistrationBean<>();        filterRegistrationBean.setFilter(new ShallowEtagHeaderFilter());        filterRegistrationBean.addUrlPatterns("/image/*", "/profile/*");        return filterRegistrationBean;    }}