mapper.update:
UPDATE member
SET nickname = #{nickname}, introduction = #{introduction}, email = #{email}, gender = #{gender}, profile_image = #{profileImage}, updated_date = CURRENT_TIMESTAMP
WHERE user_id = #{userId};

UPDATE member
SET password = #{newPassword}, salt = #{newSalt}, updated_date = CURRENT_TIMESTAMP
WHERE user_id = #{userId};

UPDATE member
SET profile_image = null
WHERE id = #{id};

UPDATE member
SET profile_image = #{profileImage}
WHERE id = #{id};
