mapper.select:
SELECT COUNT(*)
FROM post
WHERE member_id = #{memberId};

SELECT ,

FROM post AS p
INNER JOIN postimage AS i
ON p.id = i.post_id
WHERE p.id = #{id}
ORDER BY i.priority ASC;

SELECT url
FROM postimage
WHERE post_id = #{postId} AND priority = 1;

mapper.update:
UPDATE post
SET content = #{content}, status = #{status}, updated_date = CURRENT_TIMESTAMP
WHERE id = #{id};
